import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [clients, setClients] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [searchResult, setSearchResult] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/db')
      .then(response => {
        setClients(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleSearch = () => {
    // Perform search based on searchValue and update searchResult state
    // You can use axios to send a request to your backend API for each search option
    // For simplicity, I'll just update searchResult with a message for each option
    const searchOptions = [
      'List all products in stock.',
      'Show details of a specific order, including the purchased items.',
      'Find customers who have spent more than a certain amount on purchases.',
      'Calculate total sales per product category.',
      'Show items sold on a specific date.'
    ];
    const result = searchOptions.map(option => {
      return `${option}: No data available.`;
    });
    setSearchResult(result);
  };

  return (
    <div>
      <h1>Clients</h1>
      <ul>
        {clients.map(client => (
          <li key={client.id_cliente}>
            <strong>Name:</strong> {client.nome_cliente}<br />
            <strong>Address:</strong> {client.endereco}<br />
            <strong>Phone:</strong> {client.telefone}<br />
            <strong>Email:</strong> {client.email}<br />
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          placeholder="Enter search data..."
          value={searchValue}
          onChange={e => setSearchValue(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div>
        <h2>Search Results</h2>
        <ul>
          {searchResult.map((result, index) => (
            <li key={index}>{result}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
