      .then(response => {
        setClients(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div>
      <h1>Estoque</h1>
      <button>
        Exibir Estoque
      </button>

      <h1>Pedidos</h1>
      <button>
        Exibir Pedidos
